import React, { useEffect, useState, useRef } from 'react';
import { useSocket } from '../Context/SocketProvider';
import Button from '@mui/material/Button';
import SimplePeer from 'simple-peer';

const RoomPage = () => {
  const [remoteSocketId, setRemoteSocketId] = useState(null);
  const [myStream, setMyStream] = useState(null);
  const [remoteStream, setRemoteStream] = useState(null);
  const socket = useSocket();
  const remoteVideoRef = useRef(null);

  const handleUserJoined = ({ email, id }) => {
    console.log(`Email ${email} has joined the meeting`);
    setRemoteSocketId(id);
  };

  const initiatePeerConnection = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({
      audio: true,
      video: true,
    });
    setMyStream(stream);

    // Initialize the WebRTC peer connection
    const peer = new SimplePeer({ initiator: true, stream });

    peer.on('signal', (data) => {
      // Send the offer to the remote peer
      socket.emit('user:joined', { to: remoteSocketId, offer: data });
    });

    peer.on('stream', (remoteStream) => {
      // Handle the remote stream when it becomes available
      setRemoteStream(remoteStream);
    });
  };

  useEffect(() => {
    socket.on('user:joined', handleUserJoined);

    return () => {
      socket.off('room:join', handleUserJoined);
    };
  }, [socket]);

  return (
    <div>
      <h1>Room Page</h1>
      <h4>
        {remoteSocketId
          ? 'Connected'
          : 'No one in the meeting, please wait for someone to join.'}
      </h4>
      {remoteSocketId && (
        <Button
          type="submit"
          variant="contained"
          color="success"
          onClick={initiatePeerConnection}
        >
          Start the Meeting
        </Button>
      )}
      {myStream && (
  <video
    autoPlay
    muted
    playsInline
    height={'400px'}
    width={'450px'}
    srcObject={myStream} // Verify that myStream is a valid MediaStream
  />
)}

{remoteStream && (
  <video
    ref={remoteVideoRef}
    autoPlay
    playsInline
    height={'400px'}
    width={'450px'}
    srcObject={remoteStream} // Verify that remoteStream is a valid MediaStream
  />
)}
    </div>
  );
};

export default RoomPage;
