import React, { useEffect, useCallback, useState, useRef } from 'react';
import { useSocket } from '../Context/SocketProvider';
import ReactPlayer from 'react-player';
import Peer from '../Service/Peer';
import Button from '@mui/material/Button';

const RoomPage = () => {
  const [remoteSocketId, setRemoteSocketId] = useState(null);
  const [myStream, setMyStream] = useState(null);
  const [remoteStream, setRemoteStream] = useState(null);
  const socket = useSocket();
  const remoteVideoRef = useRef(null); // Ref for the remote video element

  const handleUserJoined = useCallback(({ email, id }) => {
    console.log(`Email ${email} has joined the meeting`);
    setRemoteSocketId(id);
  }, []);

  const handleCallUser = useCallback(async () => {
    const stream = await navigator.mediaDevices.getUserMedia({
      audio: true,
      video: true,
    });

    const offer = await Peer.getOffer();
    socket.emit('user:joined', { to: remoteSocketId, offer });
    setMyStream(stream);

    Peer.onRemoteStream((remoteStream) => {
      // Handle the remote stream when it becomes available
      setRemoteStream(remoteStream);
    });
  }, [remoteSocketId, socket]);

  useEffect(() => {
    socket.on('user:joined', handleUserJoined);

    return () => {
      socket.off('room:join', handleUserJoined);
    };
  }, [socket, handleUserJoined]);

  return (
    <div>
      <h1>Room Page</h1>
      <h4>
        {remoteSocketId
          ? 'Connected'
          : 'No one in the meeting please wait for someone to join.'}
      </h4>
      {remoteSocketId && (
        <Button
          type="submit"
          variant="contained"
          color="success"
          onClick={handleCallUser}
        >
          Start the Meeting
        </Button>
      )}
      {myStream && (
        <ReactPlayer
          playing
          muted
          height={'400px'}
          width={'450px'}
          url={URL.createObjectURL(myStream)} // Use the stream URL
        />
      )}
      {remoteStream && (
        <video
          ref={remoteVideoRef}
          autoPlay
          playsInline
          height={'400px'}
          width={'450px'}
        />
      )}
    </div>
  );
};

export default RoomPage;
