import React, {useEffect, useCallback, useState} from 'react';
import { useSocket } from '../Context/SocketProvider';
import ReactPlayer from "react-player";
import Button from '@mui/material/Button';


const RoomPage = () => {
    const [remoteSocketId, setRemoteSocketId] = useState(null);
    const [myStream, setMyStream] = useState();
    const socket =useSocket();

    const handleUserJoined = useCallback(({email, id}) => {
        console.log(`Email ${email} has joined the meeting`);
        setRemoteSocketId(id);
    }, []);

    const handleCallUser = useCallback( async() => {
        const stream = await navigator.mediaDevices.getUserMedia({
            audio: true, 
            video: true,
        });
        setMyStream(stream);
    }, []);

    

    useEffect(() => {
        socket.on('user:joined', handleUserJoined);

        return () => { socket.off('room:join', handleUserJoined)}
    }, [socket, handleUserJoined]);

  return (
    <div>
      <h1>Room Page</h1>
      <h4>{remoteSocketId ? "Connected" : "No one in the meeting please wait for someone to join."}</h4>
      {
        remoteSocketId && <Button type="submit" variant="contained" color="success" onClick={handleCallUser}>Start the Meeting</Button>
      }
      {
        myStream && <ReactPlayer playing muted height={'800px'} width={"500px"}url={myStream} />
      }
    </div>
  )
}

export default RoomPage
