import React, { useEffect, useCallback, useState } from 'react';
import { useSocket } from '../Context/SocketProvider';
import Button from '@mui/material/Button';
import Peer from 'peerjs';

const RoomPage = () => {
  const [remoteSocketIds, setRemoteSocketIds] = useState([]);
  const [myStream, setMyStream] = useState(null);
  const [remoteStreams, setRemoteStreams] = useState({});
  const socket = useSocket();
  const peer = new Peer();

  const handleUserJoined = useCallback(({ email, id }) => {
    console.log(`Email ${email} has joined the meeting`);
    setRemoteSocketIds((prevIds) => [...prevIds, id]);
  }, []);

  const handleCallUsers = useCallback(async () => {
    const stream = await navigator.mediaDevices.getUserMedia({
      audio: true,
      video: true,
    });
    setMyStream(stream);

    // Call each remote user using PeerJS
    remoteSocketIds.forEach((remoteSocketId) => {
      const call = peer.call(remoteSocketId, stream);

      // Handle the call
      call.on('stream', (remoteStream) => {
        // Handle the remote user's stream
        console.log('Remote stream received');
        setRemoteStreams((prevStreams) => ({
          ...prevStreams,
          [remoteSocketId]: remoteStream,
        }));
      });
    });
  }, [remoteSocketIds, peer]);

  useEffect(() => {
    socket.on('user:joined', handleUserJoined);

    return () => {
      socket.off('user:joined', handleUserJoined);
    };
  }, [socket, handleUserJoined]);

  return (
    <div>
      <h1>Room Page</h1>
      <h4>
        {remoteSocketIds.length
          ? `Connected (${remoteSocketIds.length} participants)`
          : 'No one in the meeting, please wait for someone to join.'}
      </h4>
      {remoteSocketIds.length > 0 && (
        <Button
          type="submit"
          variant="contained"
          color="success"
          onClick={handleCallUsers}
        >
          Start the Meeting
        </Button>
      )}
      {myStream && (
  <video
    autoPlay
    muted
    height={400}
    width={450}
    ref={(videoRef) => {
      if (videoRef) {
        videoRef.srcObject = myStream;
      }
    }}
  />
)}

{Object.entries(remoteStreams).map(([socketId, stream]) => (
  <video
    key={socketId}
    autoPlay
    height={400}
    width={450}
    ref={(videoRef) => {
      if (videoRef) {
        videoRef.srcObject = stream;
      }
    }}
  />
))}
    </div>
  );
};

export default RoomPage;
